import React, { useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import { AppStore } from "../context/AppStore";

const LoginPage = () => {
  const { search } = useLocation();
  const redirectUrl = new URLSearchParams(search).get("redirect");
  const redirect = redirectUrl ? redirectUrl : "/";

  const { appState, appDispatch } = useContext(AppStore);
  console.log(appState);

  return (
    <div className="sm:grid place-items-center px-4 sm:px-0">
      <p className="text-center mb-10">Login Page</p>
      <form className="flex flex-col sm:grid place-items-center bg-gray-200 gap-3 px-4 sm:px-4 py-8">
        <input
          type="text"
          placeholder="email"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 bg-purple-300 pl-1  rounded-md"
        />
        <input
          type="text"
          placeholder="password"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 bg-purple-300 pl-1  rounded-md"
        />
        <button className="text-white bg-purple-500 hover:bg-purple-700 hover:shadow-lg hover:shadow-purple-600/50 w-[180px] sm:w-[180px] h-[30px] rounded-lg ">
          Login
        </button>
        <Link
          to={`/signup?redirect=${redirect}`}
          className="mt-3 text-xs text-gray-400"
        >
          Sign Up
        </Link>
      </form>
    </div>
  );
};

export default LoginPage;
