import axios from "axios";
import React from "react";
import { useEffect } from "react";
import { useReducer } from "react";
import { useParams } from "react-router-dom";

import { FaCartPlus } from "react-icons/fa";
import { useContext } from "react";
import { Store } from "../context/Store";

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "REQUEST_PRODUCT":
      return {
        ...state,
        loading: true,
      };
    case "FETCH_PRODUCT":
      return {
        ...state,
        product: action.payload,
        loading: false,
      };
    case "FAIL_PRODUCT":
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};

const ProductDetails = () => {
  const { Pid } = useParams();

  const contextData = useContext(Store);
  const { cartState, cartDispatch } = contextData;
  console.log(cartState);
  const [state, dispatch] = useReducer(reducer, {
    product: null,
    loading: false,
    error: null,
  });
  const { product, loading, error } = state;
  const { name, image, brand, price, description, catagory, stock } = product;
  useEffect(() => {
    const getProduct = async () => {
      try {
        dispatch({ type: "REQUEST_PRODUCT" });
        const { data } = await axios.get("/data/" + Pid);
        console.log(data.products);
        dispatch({ type: "FETCH_PRODUCT", payload: data.products });
      } catch (error) {
        dispatch({ type: "FAIL_PRODUCT", payload: error.message });
      }
    };

    getProduct();
  }, [dispatch, Pid]);

  return loading ? (
    <p>loading</p>
  ) : error ? (
    <p>{error}</p>
  ) : (
    <section className="flex justify-between flex-wrap">
      {product && (
        <div>
          <img src={image} className="h-[300px] w-[300px]" alt="" />
          <p>{name}</p>
          <p>{description}</p>
          <p>{catagory}</p>
          <p>{brand}</p>
        </div>
      )}
      <div className="flex ">
        <p>{price}</p>
        <p>{stock}</p>
        <button
          onClick={() =>
            cartDispatch({ type: "ADD_TO_CART", payload: product })
          }
          className="flex items-center gap-2 h-[35px] px-4 bg-purple-500 hover:bg-purple-700 text-white hover:shadow-lg hover:shadow-purple-500/50"
        >
          Add To Cart <FaCartPlus />
        </button>
      </div>
    </section>
  );
};

export default ProductDetails;
