import React from "react";
import { Link, useLocation } from "react-router-dom";

const LoginPage = () => {
  const { search } = useLocation();
  const redirectUrl = new URLSearchParams(search).get("redirect");
  const redirect = redirectUrl ? redirectUrl : "/";
  return (
    <div className="sm:grid place-items-center">
      <p className="text-center mb-10">Login Page</p>
      <form className="flex flex-col sm:grid place-items-center bg-gray-200 gap-3 px-4 sm:px-4 py-8">
        <input
          type="text"
          placeholder="email"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 bg-purple-300 pl-1  rounded-md"
        />
        <input
          type="text"
          placeholder="password"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 bg-purple-300 pl-1  rounded-md"
        />
        <button>Login</button>
        <Link to={`/signup?redirect=${redirect}`}>Sign Up</Link>
      </form>
    </div>
  );
};

export default LoginPage;
