import axios from "axios";
import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { AppStore } from "../context/AppStore";
import { toast } from "react-toastify";

const SignUpPage = () => {
  const { search } = useLocation();
  const redirectUrl = new URLSearchParams(search).get("redirect");
  const redirect = redirectUrl ? redirectUrl : "/";
  const { AppState, AppDispatch } = useContext(AppStore);

  const token = AppState?.userInfo?.token;

  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    if (password != confirmPassword) {
      toast.error("password Not Matched");
    } else {
      try {
        const { data } = await axios.post("http://localhost:9000/api/signup", {
          name,
          email,
          password,
        });
        AppDispatch({ type: "USER_SIGNUP", payload: data });
        // localStorage.setItem("userInfo", JSON.stringify(data));
        navigate(redirect || "/");
        toast.success("Register success");
      } catch (error) {
        toast.error(error.response.data.message);
      }
    }
  };

  useEffect(() => {
    if (token) {
      navigate(redirect);
    }
  }, [redirect, navigate, token]);

  console.log(AppState);

  return (
    <div className="sm:grid place-items-center px-4 sm:px-0">
      <p className="text-center mb-10 border-b-2 text-purple-500 border-purple-500">
        Sign up
      </p>
      <form
        onSubmit={handleSignup}
        className="flex flex-col sm:grid place-items-center bg-gray-200 gap-3 px-4 sm:px-4 py-8"
      >
        <input
          type="text"
          onChange={(e) => setName(e.target.value)}
          placeholder="Full Name"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 border outline-none border-purple-500 pl-1  rounded-md"
        />

        <input
          type="text"
          onChange={(e) => setEmail(e.target.value)}
          placeholder="email"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 border outline-none border-purple-500 pl-1  rounded-md"
        />

        <input
          type="text"
          onChange={(e) => setPassword(e.target.value)}
          placeholder="password"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 border outline-none border-purple-500 pl-1  rounded-md"
        />
        <input
          type="text"
          onChange={(e) => setConfirmPassword(e.target.value)}
          placeholder="Confirm Password"
          className="h-[25px] sm:h-[34px] w-full  mx-0 sm:mx-3 border outline-none border-purple-500 pl-1  rounded-md"
        />
        <button
          onClick={handleSignup}
          className="text-white bg-purple-500 hover:bg-purple-700 hover:shadow-lg outline-none mt-3 hover:shadow-purple-600/50 w-[180px] sm:w-[180px] h-[30px] rounded-lg "
        >
          Signup
        </button>

        <Link
          to={`/signin?redirect=${redirect}`}
          className="mt-3 text-xs text-purple-400"
        >
          Already Have an Acount
        </Link>
      </form>
    </div>
  );
};

export default SignUpPage;
