import React from "react";
import { useNavigate } from "react-router-dom";

const CardComponent = ({ it }) => {
  const navigate = useNavigate();
  return (
    <div
      className="shadow-md flex flex-col h-[320px] w-[230px]"
      onClick={(e) => navigate("/product-detail/" + it._id)}
    >
      <img src="../../../logo512.png" className="h-[220px] w-full" alt="" />
      <div className="flex flex-col text-sm text-gray-600 capitalize">
        <p>{it.name}</p>
        <p>{it.brand}</p>
        <button onClick={() => {}} className="text-white bg-purple-500">
          Add To Cart
        </button>
      </div>
    </div>
  );
};

export default CardComponent;
