import axios from "axios";
import React from "react";
import { useEffect } from "react";
import { useReducer } from "react";
import { useParams } from "react-router-dom";

import { FaCartPlus } from "react-icons/fa";

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "REQUEST_PRODUCT":
      return {
        ...state,
        loading: true,
      };
    case "FETCH_PRODUCT":
      return {
        ...state,
        product: action.payload,
        loading: false,
      };
    case "FAIL_PRODUCT":
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};

const ProductDetails = () => {
  const { Pid } = useParams();

  const [state, dispatch] = useReducer(reducer, {
    product: null,
    loading: false,
    error: null,
  });

  const { product, loading, error } = state;

  useEffect(() => {
    const getProduct = async () => {
      try {
        dispatch({ type: "REQUEST_PRODUCT" });
        const { data } = await axios.get("/data/" + Pid);
        console.log(data.products);
        dispatch({ type: "FETCH_PRODUCT", payload: data.products });
      } catch (error) {
        dispatch({ type: "FAIL_PRODUCT", payload: error.message });
      }
    };

    getProduct();
  }, [dispatch, Pid]);
  return (
    <div className="flex justify-between">
      {product && (
        <div>
          <img src={product.image} className="h-[300px] w-[300px]" alt="" />
          <p>{product.name}</p>
          <p>{product.price}</p>
          <p>{product.description}</p>
          <p>{product.catagory}</p>
          <p>{product.brand}</p>
          <p>{product.stock}</p>
        </div>
      )}
      <div className="flex">
        <button className="flex items-center gap-2 py-1 px-4 bg-purple-500 hover:bg-purple-700 text-white shadow-lg">
          Add To Cart <FaCartPlus />
        </button>
      </div>
    </div>
  );
};

export default ProductDetails;
