import React, { useEffect } from "react";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import ProgressSteps from "../components/ProgressSteps";
import { AppStore } from "../context/AppStore";
import { CartStore } from "../context/CartStore";

const OrderPage = () => {
  const { AppState, AppDispatch } = useContext(AppStore);
  const { state, dispatch } = useContext(CartStore);

  const navigate = useNavigate();

  //   console.log(state?.cart?.cartItems);
  const { fullName, address, postalCode, city, country } =
    state?.cart?.shippingInfo;

  useEffect(() => {
    if (!AppState?.userInfo?.token) {
      navigate("/login");
    }
  }, []);
  return (
    <div>
      <ProgressSteps active1 active2 active3 active4 />
      <p className="text-center text-lg sm:text-3xl text-purple-500">
        show Order
      </p>
      <section className="flex flex-col gap-5">
        {/* shipping Info */}
        <div className="flex flex-col border gap-2 py-3 px-2 sm:px-5">
          <p className="text-gray-700 font-bold ">Shipping Data</p>
          <p className="text-gray-700 text-sm tracking-wide">
            <span className="font-bold text-black text-xs">Name</span> :{" "}
            {fullName}
          </p>
          <p className="text-gray-700 text-sm tracking-wide">
            <span className="font-bold text-black text-xs">Address</span> :
            {`${address} ${city}  ${country} ${postalCode}`}
          </p>
        </div>
        {/* cart Items */}
        <div className="flex flex-col border gap-2 py-3 px-2 sm:px-5">
          {state?.cart?.cartItems.map((it) => {
            const { name, price, quantity, image } = it;
            return (
              <div className="flex justify-between items-center">
                <div className="flex items-center gap-2 w-[30%] bg-red-300">
                  <img src={image} className="h-[70px] w-[70px] rounded-md" />
                  <p>{name}</p>
                </div>
                <p className="bg-red-300  w-[30%]">{quantity}</p>
                <p className="bg-red-300  w-[30%]">{price}</p>
              </div>
            );
          })}
        </div>
      </section>
    </div>
  );
};

export default OrderPage;
