import { createContext, useReducer } from "react";

const CartStore = createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case "ADD_TO_CART":
      return {
        ...state,
        itmes: [...state, action.payload],
      };
  }
};

export const CartStoreProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, {
    items: [{ id: 1, name: "khial kava", rating: 2, price: 79 }],
    loading: false,
    error: null,
  });
  const value = { state, dispatch };
  return <CartStore.Provider value={value}>{children}</CartStore.Provider>;
};
