import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AppSpinner from "../components/AppSpinner";
import { CartStore } from "../context/CartStore";
import { FaPlusCircle, FaMinusCircle, FaTrashAlt } from "react-icons/fa";
const CartPage = () => {
  const { state } = useContext(CartStore);
  const {
    cart: { cartItems },
  } = state;
  console.log(cartItems);

  return (
    <section className="flex gap-5 sm:gap-10 flex-wrap px-2 sm:px-6">
      <div className=" flex  flex-col justify-center gap-5 sm:gap-10 w-[98%] sm:w-[60%] ">
        {cartItems.length === 0 ? (
          <div>
            <p>cart is empty </p>
            <Link to={"/"}>go to Home</Link>
          </div>
        ) : (
          cartItems.map((it) => {
            return (
              <div className="flex justify-around items-center h-[80px] border shadow-lg">
                <img
                  src={it.image}
                  alt={it.name}
                  className="h-[60px] w-[80px] rounded-lg border-2 "
                />
                <p className="text-gray-700 text-sm">{it.name}</p>
                <p className="text-gray-500 text-sm">${it.price}</p>
                <div className="flex items-center gap-2">
                  <button className="text-gray-500 text-xl sm:text-2xl">
                    <FaPlusCircle />
                  </button>
                  <p>{it.quantity}</p>
                  <button className="text-gray-500 text-xl sm:text-2xl">
                    <FaMinusCircle />
                  </button>
                </div>
                <p>
                  <span>
                    <FaTrashAlt />
                  </span>
                </p>
              </div>
            );
          })
        )}
      </div>
      <div className="flex flex-col w-[98%] sm:w-[30%]">
        <p>Total-Items: {cartItems.reduce((a, c) => a + c, 0)}</p>
        <p>Total-Price: {cartItems.reduce((a, c) => a + c, 0)}</p>
      </div>
    </section>
  );
};

export default CartPage;
