import axios from "axios";
import React from "react";
import { useContext } from "react";
import { useReducer } from "react";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { CartStore } from "../context/CartStore";
import { Store } from "../context/Store";

export const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return {
        ...state,
        loading: true,
      };
    case "FETCH_SUCCESS":
      return {
        ...state,
        products: action.payload,
        loading: false,
      };
    case "FETCH_FAIL":
      return {
        ...state,
        error: action.payload,
      };
    default:
      return state;
  }
};

const Home = () => {
  const navigate = useNavigate();
  const states = useContext(CartStore);

  const newcartState = state.state;

  const { cartState } = useContext(Store);
  console.log(newCartState);
  const [state, dispatch] = useReducer(reducer, {
    products: [],
    loading: false,
    error: null,
  });
  const { products, loading, error } = state;

  useEffect(() => {
    const allProducts = async () => {
      try {
        dispatch({ type: "FETCH_REQUEST" });
        const { data } = await axios.get("/data");
        dispatch({ type: "FETCH_SUCCESS", payload: data.products });
      } catch (error) {
        dispatch({ type: "FETCH_FAIL", payload: error.message });
      }
    };

    allProducts();
  }, []);

  return (
    <div className="flex justify-center flex-wrap gap-4 sm:gap-7">
      {loading ? (
        <p>loading....</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        products.map((it) => {
          return (
            <div
              className="shadow-md flex flex-col h-[320px] w-[230px]"
              onClick={(e) => navigate("/product-detail/" + it._id)}
            >
              <img
                src="../../../logo512.png"
                className="h-[220px] w-full"
                alt=""
              />
              <div className="flex flex-col">
                <p>{it.name}</p>
                <p>{it.brand}</p>
                <p>{it.catagory}</p>
              </div>
            </div>
          );
        })
      )}
    </div>
  );
};

export default Home;
