import React, { useContext } from "react";
import {
  FaCartArrowDown,
  FaFacebook,
  FaHamburger,
  FaInstagram,
  FaTimes,
  FaWhatsapp,
} from "react-icons/fa";

import "../styles/Navbar.css";

import { Link } from "react-router-dom";
import { useState } from "react";
import { CartStore } from "../context/CartStore";
const Navbar = () => {
  const [icon, setIcon] = useState(false);
  const { state } = useContext(CartStore);
  const {
    cart: { cartItems },
  } = state;
  return (
    <section className="navbar__main">
      {/* logo */}
      <div className="navbar__logo">
        <p>
          <Link to={"/"}>All-Hadi</Link>
        </p>
        <span onClick={() => setIcon(!icon)} className="navbar__icon">
          <FaHamburger />
        </span>
      </div>
      {/* navbar for toggle  */}
      <nav
        onClick={() => setIcon(!icon)}
        className={icon ? "navbar__menu navbar__active" : "navbar__menu"}
      >
        <div className="navbar__items__container">
          <Link className="navbar__item" to="/">
            Home
          </Link>
          <Link className="navbar__item" to="/">
            Login
          </Link>
          <Link className="navbar__item" to="/">
            Sign-Up
          </Link>
        </div>
        <div className="flex gap-5 sm:gap-10">
          {/* social Icons  */}
          <div className="navbar__social__icons">
            <span>
              <FaFacebook />
            </span>
            <span>
              <FaWhatsapp />
            </span>
            <span>
              <FaInstagram />
            </span>
          </div>
          <Link className="navbar__cart" to="/cart">
            <FaCartArrowDown />
            <span>{cartItems.length}3</span>
          </Link>
        </div>
      </nav>
    </section>
  );
};

export default Navbar;
