import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AppSpinner from "../components/AppSpinner";
import { CartStore } from "../context/CartStore";
import { FaPlus, FaMinus, FaTrashAlt } from "react-icons/fa";
const CartPage = () => {
  const { state } = useContext(CartStore);
  const {
    cart: { cartItems },
  } = state;
  console.log(cartItems);

  return (
    <div className=" flex justify-center flex-wrap gap-5 sm:gap-10">
      {cartItems.length === 0 ? (
        <div>
          <p>cart is empty </p>
          <Link to={"/"}>go to Home</Link>
        </div>
      ) : (
        cartItems.map((it) => {
          return (
            <div className="flex justify-around items-center h-[80px] w-[98%] sm:w-[60%] border shadow-lg">
              <img
                src={it.image}
                alt={it.name}
                className="h-[60px] w-[80px] rounded-lg border-2 "
              />
              <p>{it.name}</p>
              <p>{it.price}</p>
              <div className="flex">
                <span>
                  <FaPlus />
                </span>
                <p>{it.quantity}</p>
                <span>
                  <FaMinus />
                </span>
              </div>
              <p>
                <span>
                  <FaTrashAlt />
                </span>
              </p>
            </div>
          );
        })
      )}

      <div className="flex flex-col w-[98%] sm:w-[30%]">
        <p>Total-Items: {cartItems.reduce((a, c) => a + c, 0)}</p>
        <p>Total-Price: {cartItems.reduce((a, c) => a + c, 0)}</p>
      </div>
    </div>
  );
};

export default CartPage;
