import axios from "axios";
import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { AppStore } from "../context/AppStore";
import { toast } from "react-toastify";
import AppInput from "../components/AppForms/AppInput";

const LoginPage = () => {
  const { search } = useLocation();
  const redirectUrl = new URLSearchParams(search).get("redirect");
  const redirect = redirectUrl ? redirectUrl : "/";
  const { AppState, AppDispatch } = useContext(AppStore);

  const token = AppState?.userInfo?.token;

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const { data } = await axios.post("http://localhost:9000/api/signin", {
        email,
        password,
      });
      AppDispatch({ type: "USER_SIGNIN", payload: data });
      localStorage.setItem("userInfo", JSON.stringify(data));
      navigate(redirect || "/");
      toast.success("log in success");
    } catch (error) {
      toast.error(error.response.data.message);
    }
  };

  useEffect(() => {
    if (token) {
      navigate(redirect);
    }
  }, [redirect, navigate, token]);

  console.log(AppState);

  return (
    <div className="sm:grid place-items-center px-4 sm:px-0">
      <p className="text-center mb-10 border-b-2 text-purple-500 border-purple-500">
        Login Page
      </p>
      <form
        onSubmit={handleLogin}
        className="flex flex-col sm:grid place-items-center bg-gray-200 gap-3 px-4 sm:px-4 py-8"
      >
        <AppInput
          type="text"
          onChange={(e) => setEmail(e.target.value)}
          placeholder="email"
        />

        <AppInput
          type="password"
          onChange={(e) => setPassword(e.target.value)}
          placeholder="password"
        />

        <button
          onClick={handleLogin}
          className="text-white bg-purple-500 hover:bg-purple-700 hover:shadow-lg outline-none mt-3 hover:shadow-purple-600/50 w-[180px] sm:w-[180px] h-[30px] rounded-lg "
        >
          Login
        </button>

        <Link
          to={`/signup?redirect=${redirect}`}
          className="mt-3 text-xs text-purple-400"
        >
          Sign Up
        </Link>
      </form>
    </div>
  );
};

export default LoginPage;
